CREATE TABLE "public"."tag_languages" ("id" serial NOT NULL, "tag_id" integer NOT NULL, "language_id" integer NOT NULL, "value" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("tag_id") REFERENCES "public"."tags"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("language_id") REFERENCES "public"."languages"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("tag_id", "language_id"));
